INSERT INTO `course` VALUES ('218', '十送红军', '3', '1', '1', '1', '20', '十送红军知识点', '<p style=\"white-space: normal;\">【教学目标】<span style=\"color: rgb(51, 51, 51); font-family: *ri*l, t*hom*, &#39;microsoft yahei&#39;, 宋体, sans-serif; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);\">《十送红军》是为了纪念红军长征八十周年而制作的献礼剧</span></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\">【教学重点】<span style=\"color: rgb(51, 51, 51); font-family: *ri*l, 宋体, sans-serif; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);\">十个故事各自独立，十个人物相互关联，随着《十送红军》的剧情发展，观众将重走长征路，了解在瑞金挥师、</sp*n><* target=\"_blank\" href=\"http://baike.baidu.com/item/%e6%b9%98%e6%b1%9f%e6%88%98%e5%bd%b9\" style=\"color: rgb(19, 110, 194); text-decoration: none; font-family: *ri*l, 宋体, sans-serif; line-height: 24px; text-indent: 28px; white-space: norm*l; b*ckground-color: rgb(255, 255, 255);\">湘江战役</*><sp*n style=\"color: rgb(51, 51, 51); font-family: *ri*l, 宋体, sans-serif; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);\">、</sp*n><* target=\"_blank\" href=\"http://baike.baidu.com/item/%e9%81%b5%e4%b9%89%e4%bc%9*%e8%*e%ae/64086\" d*t*-lemmaid=\"64086\" style=\"color: rgb(19, 110, 194); text-decoration: none; font-family: *ri*l, 宋体, sans-serif; line-height: 24px; text-indent: 28px; white-space: norm*l; b*ckground-color: rgb(255, 255, 255);\">遵义会议</*><sp*n style=\"color: rgb(51, 51, 51); font-family: *ri*l, 宋体, sans-serif; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);\">、</sp*n><* target=\"_blank\" href=\"http://baike.baidu.com/item/%e5%9b%9b%e6%b8%a1%e8%b5%a4%e6%b0%b4/11590\" d*t*-lemmaid=\"11590\" style=\"color: rgb(19, 110, 194); text-decoration: none; font-family: *ri*l, 宋体, sans-serif; line-height: 24px; text-indent: 28px; white-space: norm*l; b*ckground-color: rgb(255, 255, 255);\">四渡赤水</*><sp*n style=\"color: rgb(51, 51, 51); font-family: *ri*l, 宋体, sans-serif; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);\">、飞夺泸定、翻越雪山、跋涉草地、吴起会师等重大历史事件背后普通战士的点点滴滴</span></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\">【教学难点】<span style=\"color: rgb(51, 51, 51); font-family: *ri*l, t*hom*, &#39;microsoft yahei&#39;, 宋体, sans-serif; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);\">《十送红军》以长征为线索，穿插着讲述了十个普通红军战士的故事。他们不同年龄、性别、兵种，却有着共同愿望，那就是用自己的生命去守护和传递红色的星星火种。</span></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\">【教学过程】<span style=\"color: rgb(51, 51, 51); font-family: *ri*l, 宋体, sans-serif; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);\">《十送红军》分为十单元，讲述了十位不同年龄、性别、兵种的红军战士，在长征途中成长、蜕变、抗争与牺牲的故事。</span></p>', '0', '8', '/uploads/temporary/suofang1490078205.png', '0', '0', '0', '1', '0', '0', '0', '0', '0', null, '2017-03-21 14:39:53', '2017-03-21 15:23:08', null, null);
