-- ----------------------------
-- Table structure for `usermessage`
-- ----------------------------
DROP TABLE IF EXISTS `usermessage`;
CREATE TABLE `usermessage` (
  `id` int(8) NOT NULL AUTO_INCREMENT,
  `username` varchar(80) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '接收者username',
  `actionId` int(8) DEFAULT NULL COMMENT '资源ID 用于拼接跳转的URL',
  `courseType` tinyint(1) DEFAULT NULL COMMENT '1、作业 2、测验',
  `type` tinyint(1) DEFAULT NULL COMMENT '0后台手动输入\r\n1新的课程开课通知(学生收)\r\n2有提交截止时间要求的作业/测验任务通知(学生收)\r\n3尚未提交作业/测验的提醒(提前两个小时通知)(学生收)\r\n4课程中对老师的提问被回答(学生收)\r\n5关注的教师发布新课程(都收)\r\n6提问被回答(社区)(都收)\r\n7回答被评论(社区)(都收)\r\n8发布的资源被评论(老师收)\r\n9发布的课程通过审核(老师收)\r\n10发布的课程未通过审核(老师收)\r\n11发布的课程被提问(老师收)\r\n12发布的作业/测验已到截止提交时间(老师收)',
  `tempId` int(8) DEFAULT '0' COMMENT ' 0 -> 前台的消息  \r\n非0 -> usermessagetem表外键',
  `content` text CHARACTER SET utf8 COLLATE utf8_unicode_ci COMMENT '信息内容',
  `client_ip` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '客户端IP地址',
  `fromUsername` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '消息来源用户',
  `toUsername` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '其他相关用户',
  `isRead` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0 -> 未读  1 -> 已读',
  `created_at` datetime DEFAULT NULL,
  `display` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否显示该消息',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=1295 DEFAULT CHARSET=utf8 COMMENT='用户通知表';

